mapper.select:
select * 
from (select rownum r_num, a.* 
	from ( select * 
			from notice
			order by notice_idx desc 
		 ) a 
	) 
where r_num between #{begin} and #{end}

mapper.update:
		update notice set notice_title=#{notice_title},notice_content=#{notice_content}, notice_file_name = null,notice_refile_name = null
      , notice_file_name = #{ notice_file_name }, notice_refile_name = #{ notice_refile_name }
         
      where notice_idx = #{notice_idx}

mapper.select:
		SELECT * 
    		FROM (SELECT ROWNUM r_num, a.* 
        		FROM (SELECT * FROM NOTICE 
              		  WHERE notice_idx < #{notice_idx}
              		  ORDER BY notice_idx DESC 
        		) a 
    		) 
		WHERE r_num = 1

		SELECT * 
    		FROM (SELECT ROWNUM r_num, a.* 
        		FROM (SELECT * FROM NOTICE 
              		  WHERE notice_idx > #{notice_idx}
              		  ORDER BY notice_idx ASC
        		) a 
    		) 
		WHERE r_num = 1
